definition.script:
pipeline {
    agent { label 'built-in' }
    stages {
        stage('IaC Plan') {
            steps {
                sh 'cd /src/terraform && sed -i s/localhost/localstack/g providers.tf'
                sh 'cd /src/terraform && terraform init'
                sh 'cd /src/terraform && terraform plan'
            }
        }
        stage('IaC Apply') {
            steps {
                sh 'echo "Apply TF"'
                sh 'cd /src/terraform && terraform apply -auto-approve'
            }
        }
        stage('Docker Build') {
            steps {

                sh 'echo "Building docker"'
                sh 'cd /src/app && docker run  --network sa-code-test --rm -p 8085:8080 -d $(docker build -q -t jj .) '
                sh 'echo "Docker deployed and endpoint is up"'
            }
        }

    }
}
